  transition: color 250ms ease-in;

  @media screen and (-ms-high-contrast: active) {
    border: 2px solid currentcolor;
  }

  &:hover,
  &:active {
    color: $white;
  }

  &:focus {
    outline-style: solid;
    outline-color: transparent;
    box-shadow: 0 0 0 4px scale-color($blue, $lightness: -40%);
  }

  &.selected {
    color: $white;
  }
}

