sources.scss/layout/_cards.scss:
@use "../abstracts/variables" as *;
@use "../base/utilities" as *;
@use "../abstracts/mixins" as *;

// PERSON
.person {
  &__card {
    display: grid;
    grid-template-columns: 0.5fr 1fr;
    grid-template-rows: auto;
  }

  &__avatar {
    grid-column: 1/2;
    grid-row: 1/3;
  }

  &__period {
    display: flex;
    justify-content: space-around;
  }
}

// CARDS
.stats {
  display: grid;
  grid-template-rows: auto;

  .content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    row-gap: 1rem;

    h2 {
      grid-column: 1/2;
      grid-row: 1/2;
      align-self: center;
    }

    .stats__hrs {
      grid-column: 1/2;
      grid-row: 2/3;
      align-self: center;
    }

    .prev__hrs {
      justify-self: end;
      align-self: center;
    }

    img {
      justify-self: end;
      align-self: center;
    }
  }
}

sources.scss/layout/_site.scss:
@use "../abstracts/variables" as *;

.site {
  display: grid;
  grid-template-columns: 1fr;
  grid-template-rows: auto;
  margin: auto;
}

@media (min-width: 768px) {
  .site {
    grid-template-columns: repeat(2, 1fr);
    grid-template-rows: auto;
    gap: 2rem;
  }

  .person {
    place-self: center;
    grid-column: 1/3;
  }

  .stats {
    place-self: center;
  }
}

@media (min-width: 1024px) {
  .site {
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: auto;
    margin-left: 4rem;
    margin-right: 4rem;
  }

  .person {
    margin: auto;
    width: 30rem;
    grid-column: 1/2;
    grid-row: 1/4;

    &__card {
      display: grid;
      grid-template-columns: 1fr;
    }

    &__avatar {
      margin-bottom: 3.15rem;
    }

    h1 {
      margin-top: 0.3rem;
      font-size: 4rem;
      line-height: 4.74rem;
      opacity: 1;
    }

    &__period {
      display: flex;
      flex-direction: column;
      gap: 2.1rem;
    }
  }

  .stats {
    margin: auto;
    width: 30rem;

    .content {
      grid-template-columns: 1fr 0.2fr;

      h2 {
        grid-column: 1/2;
        grid-row: 1/2;
        margin-bottom: 4rem;
      }

      .prev__hrs {
        grid-column: 1/3;
        justify-self: start;
        align-self: center;
      }

      .stats__hrs {
        grid-column: 1/2;
      }

      .content img {
        grid-column: 2/3;
        grid-row: 1/2;
        margin-bottom: 2.4rem;
      }
    }
  }
}

@media (min-width: 1200px) {
  .site {
    grid-template-columns: repeat(4, 25.5rem);
    grid-template-rows: auto;
    gap: 3rem;
    place-self: center;
  }

  .person {
    width: 100%;
    grid-column: 1/2;
    grid-row: 1/3;
  }

  .stats {
    width: 100%;

    h2 {
      grid-column: 1/2;
      grid-row: 1/2;
    }

    .prev__hrs {
      grid-column: 1/3;
      justify-self: start;
      align-self: center;
    }

    .stats__hrs {
      grid-column: 1/2;
      margin-bottom: 0.8rem;
    }

    .content img {
      grid-column: 2/3;
      grid-row: 1/2;
      margin-bottom: 2.4rem;
    }
  }
}
