  display: none;
}

sources.scss/base/_base.scss:
@use "abstracts/variables" as *;

html {
  box-sizing: border-box;
  font-size: 62.5%;
}

*,
*::before,
*::after {
  box-sizing: inherit;
  margin: 0;
  padding: 0;
}

html,
body {
  height: 100%;
  display: grid;
}

body {
  font-family: $font-rubik;
  font-size: 1.8rem;
  background-color: $very-dark-blue;
}

.site {
  margin: auto;
}

sources.scss/abstracts/_variables.scss:
// COLORS

// Primary
$blue: hsl(246, 80%, 60%);
$red-work: hsl(15, 100%, 70%);
$blue-play: hsl(195, 74%, 62%);
$red-study: hsl(348, 100%, 68%);
$lime-exercise: hsl(145, 58%, 55%);
$violet-social: hsl(264, 64%, 52%);
$orange-selfcare: hsl(43, 84%, 65%);

// Neutral
$very-dark-blue: hsl(226, 43%, 10%);
$dark-blue: hsl(236, 45%, 20%);
$desaturated-blue: hsl(235, 45%, 61%);
$pale-blue: hsl(236, 100%, 87%);
$white: hsl(0, 0%, 100%);

// Fonts
$font-rubik: 'Rubik', sans-serif;

sources.scss/base/_typography.scss:
@use "../abstracts/mixins" as *;
@use "../abstracts/variables" as *;
@use "../base/utilities" as *;

// PERSON
.person {
  &__report {
    font-weight: 400;
    font-size: 1.5rem;
    line-height: 1.778rem;
    color: $pale-blue;
  }

  h1 {
    font-weight: 300;
    font-size: 2.4rem;
    line-height: 2.844rem;
    color: $white;
    opacity: 0.24;
  }

  &__period {
    color: $desaturated-blue;
    @include reset-list;

    & li {
      cursor: pointer;
      transition: color 250ms ease-in;

      &:hover {
        color: $white;
      }
    }
  }
}

// CARDS

.stats {
  color: $white;

  .content {
    h2 {
      font-weight: 500;
      font-size: 1.8rem;
      line-height: 2.133rem;
    }

    .stats__hrs {
      font-weight: 300;
      font-size: 3.2rem;
      line-height: 3.792rem;
    }
  }

  .prev__hrs {
    font-weight: 400;
    font-size: 1.5rem;
    line-height: 1.778rem;
    color: $pale-blue;
  }
}

sources.scss/abstracts/_mixins.scss:
@mixin reset-list {
  margin: 0;
  padding: 0;
  list-style-type: none;
}

@mixin bg-size-card-mobile {
  width: 100%;
  height: 3.8rem;
}

sources.scss/components/_card.scss:
@use "../abstracts/variables" as *;
@use "../base/utilities" as *;
@use "../abstracts/mixins" as *;

section {
  margin-bottom: $margin-bottom-card;
}

.stats {
  border-top-left-radius: $border-radius;
  border-top-right-radius: $border-radius;
  border-bottom-left-radius: $border-radius;
  border-bottom-right-radius: $border-radius;

  &--work {
    background-color: $red-work;
  }
  &--play {
    background-color: $blue-play;
  }
  &--study {
    background-color: $red-study;
  }
  &--exercise {
    background-color: $lime-exercise;
  }
  &--social {
    background-color: $violet-social;
  }
  &--selfcare {
    background-color: $orange-selfcare;
  }

  .bg {
    @include bg-size-card-mobile;
    border-top-left-radius: $border-radius;
    border-top-right-radius: $border-radius;
    overflow: hidden;

    img {
      max-width: 7.8rem;
      float: right;
    }
  }

  .stats--work .bg {
    background-color: $red-work;
  }
  .stats--play .bg {
    background-color: $blue-play;
  }
  .stats--study .bg {
    background-color: $red-study;
  }
  .stats--exercise .bg {
    background-color: $lime-exercise;
  }
  .stats--social .bg {
    background-color: $violet-social;
  }
  .stats--selfcare .bg {
    background-color: $orange-selfcare;
  }

  .content {
    background-color: $dark-blue;
    border-top-left-radius: $border-radius;
    border-top-right-radius: $border-radius;
    border-bottom-left-radius: $border-radius;
    border-bottom-right-radius: $border-radius;
    padding: 2.8rem 2.4rem;
  }
}

